import './App.css';
import { useEffect, useState } from 'react';
import axios from 'axios';
import { Route, Routes, useLocation } from 'react-router-dom';
import Nav from './Nav/Nav';
import Home from './Home/Home';
import Login from './LogIn/LogIn';

function App() {

  const [userData, setUserData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false);

  const getUserData = async () => {
    try {
      const response = await axios.get('https://captain-citizen-america.herokuapp.com/users');
      setUserData(response.data);
      setIsLoading(false);
    }
    catch (error) {
      setIsError(true);
    }
  }

  useEffect(() => {
    getUserData();
  }
  , []);

  const location = useLocation();

  const [user, setUser] = useState(null);

  const [loginForm, setLoginForm] = useState({
    username: '',
    password: '',
  });

  const handleLogin = (e) => {
    setLoginForm({
      ...loginForm,
      [e.target.name]: e.target.value,
    });
  };

  const validateLogin = () => {
    const userSignIn = userData.find(
      (user) => user.username === loginForm.username
    );
    if (userSignIn.password === loginForm.password) {
      console.log('welcome');
      const index = userData.indexOf(userSignIn);
      console.log(userData[index]._id);
      axios
        .put(
          `https://petstagram-backend.herokuapp.com/petstagram/users/${userData[index]._id}`,
          {
            logIn: true,
          }
        )
        .then((res) => {
          console.log(res.data);
          setUser(res.data);
        });
    } else {
      alert('The password you’ve entered is incorrect.');
    }
  };

  console.log(userData);
  return (
    <div className="App">

      <nav className='nav'>
        <Nav />
      </nav>

      <main>
        <Routes>
          <Route path="/" element={<Home userData={userData} />} />
        </Routes>
      </main>

    </div>
  );
}

export default App;
