import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './userProfile.css';

const UserProfile = ({ fakeUser }) => {
  const [post, setPost] = useState([]);

  return (
    <div className='userBoard'>
      <div className='userInfo'>
        <img className='userIcon' src={fakeUser.photo_url} />
        <div className='user'>
          <p class='userName'>Name: {fakeUser.first_name}</p>
          <p class='userName'>Last Name: </p>
          <Link to='/edit-profile'>
            <button className='userBtn'>Edit profile</button>
          </Link>
        </div>

        <div className='postQuantity'>
          <div id='post'>
            <p>0</p> <p className='text'>Posts</p>
          </div>

          <div id='followers'>
            <p>0</p> <p className='text'>Followers</p>
          </div>

          <div id='following'>
            <p>0</p> <p className='text'>Following</p>
          </div>
        </div>

      </div>

      <div className='menu'>
        <p>My Request History</p>
        <p>Help History</p>
      </div>

    </div>
  );
};

export default UserProfile;
