import React, {useState, useCallback, useEffect} from 'react'
import { GoogleMap, useJsApiLoader, MarkerF, InfoWindow, DirectionsRenderer } from '@react-google-maps/api';
import "./map.css"
import mapStyles from './mapStyles'

const containerStyle = {
  width: '100%',
  height: '100%',
  margin: '0 auto',
}

const center = {
  lat: 40.74112,
  lng: -73.98974
}

const google = window.google

function Map({userData}) {
  // console.log(props);
  const isLoaded = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyAwcOSQ6hnqoqiXX_1D1ykHOBAZZ2UorHE",
    libraries: ['places'],
  })

  const [selectedPlace, setSelectedPlace] = useState(null)
  const [distance, setDistance] = useState(null)
  const [duration, setDuration] = useState(null)
  const [currentLocation, setCurrentLocation] = useState({
    lat: 40.74112,
    lng: -73.98974
  })
  const [destination, setDestination] = useState({
    lat: null,
    lng: null
  })

  const calculateDistance = useCallback(() => {
    const origin = new google.maps.LatLng(currentLocation.lat, currentLocation.lng)
    const destination = new google.maps.LatLng(destination.lat, destination.lng)
    const service = new google.maps.DistanceMatrixService()
    service.getDistanceMatrix({
      origins: [origin],
      destinations: [destination],
      travelMode: google.maps.TravelMode.WALKING,
      unitSystem: google.maps.UnitSystem.IMPERIAL,
    }, (response, status) => {
      if (status ===  google.maps.DistanceMatrixStatus.OK) {
        const { distance, duration } = response.rows[0].elements[0]
        setDistance(distance.text)
        setDuration(duration.text)
        console.log(origin)
      }
    })
  }, [currentLocation, destination])

  const clearRoute = useCallback(() => {
    setSelectedPlace(null)
    setDistance(null)
    setDuration(null)
  }
  , [setSelectedPlace, setDistance, setDuration])



  const image = "./images/Captain_America's_Shield.png"
  const userMarker = "./images/user.png"

  const [selectedMarker, setSelectedMarker] = useState()

  // console.log(selectedMarker);
  console.log(destination);

  return isLoaded ? (
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={16}
        options={{styles: mapStyles}}
      >

        <MarkerF
          position={currentLocation}
          icon={{
            url: userMarker,
            scaledSize: new window.google.maps.Size(25, 35)
          }}
          onClick={() => {
            setSelectedMarker(currentLocation)
            setSelectedPlace(currentLocation)
          }
          }
        />

      {userData.map((user) => {
    let lat = parseFloat(user.lat);
    let lng = parseFloat(user.lng);

    return (
      <MarkerF
      key={user.id}
      position= {{lat: lat, lng: lng}} 
      icon={{
        url: image,
        scaledSize: new window.google.maps.Size(28,28)
      }}
      onClick={() => {
        setSelectedMarker(user)
        setDestination({
          ...destination,
          lat: parseFloat(selectedMarker.lat), 
          lng: parseFloat(selectedMarker.lng)
        })
        console.log(user)
      }}
      />
    )
  })
}


      {selectedMarker && 
      (<InfoWindow
      position= {{lat: parseFloat(selectedMarker.lat), lng: parseFloat(selectedMarker.lng)}}
      onCloseClick={() => {
        setSelectedMarker(null)
      }}
      >
      <div id='infoWindow'>
        User Details
        <p>Name: {selectedMarker.first_name} {selectedMarker.last_name}</p>
        <button onClick={calculateDistance}>Navigate</button>
      </div>
      </InfoWindow>
      )}

    </GoogleMap>




  ) : <h1>Loading</h1>
}
export default React.memo(Map)
