import React, { useState } from "react";
import './home.css'
import Map from '../Map/Map'

const Home = ({userData, socket, user, setNotifications}) => {
    const[isActive, setActive] = useState(false)
    const[clicked, setClicked] = useState(false)
    // console.log(clicked);
    // const handleNotification = () => {
    //     setClicked(!clicked)
    //     socket.emit("sendNotification", {
    //         senderName: user.user_id,
    //         receiverName: user.user_id,
    //     })
    // }
    handleNotification = () => {
        setClicked(!clicked)
        setNotifications({
            senderName: user.user_id,
            receiverName: user.user_id,
        })
    }

    return(
        <div className="home">
            <div className="alertBox">
                { !clicked ?
                    <h1 id="alert">
                        You Have <span id="amount">5</span> Captain Citizens Within 1 Mile
                    </h1>
                    : <h1 id="emergency"> Help Requested, Be Safe! </h1>
                    }
            </div>
             <div className="map">
                <Map userData={userData}/>
            </div>
            <div className="btnBox">
                {!clicked ?
                    <button class="depth" type="button" onClick={() => handleNotification()}> <p className="gradientText">Emergency</p> </button> : <button class="depth" type="button" onClick={setClicked(!clicked)}><p className="gradientText">Cancel</p></button>
                }
            </div>
        </div>
    )
}

export default Home;
