import React, { useEffect, useState } from "react";
import './nav.css'
import { FaRegUserCircle, FaRegBell, FaHome } from 'react-icons/fa';
import { Link } from "react-router-dom";
import { Socket } from "socket.io-client";


const Nav = ({user, socket}) => {
    const[isActive, setActive] = useState(false)
    const [notifications, setNotifications] = useState([])

    const handleToggle = () => {
        setActive(!isActive)
      };

    useEffect(() => {
        socket?.on("getNotification", data => {
            setNotifications((prev) => [...prev, data])
        }
        , [socket])
    })

    const displayNotifications = () => {
        let action;
        if (notifications.length > 0) {
            action = notifications.map((notification, index) => {
                return (
                    <div key={index} className="notifications">
                        <p>{notification.senderName}</p>
                        <button>Navigate</button>
                    </div>
                )
            }
            )
        }}

    return(
        <header>
            <div className="navBox">
                <div className="navLogo">
                    <img src="/images/Captain_America's_Shield.png" alt="Captain America's Shield" id="shield"/>
                    <h1 className="gradient-text">Captain Citizen America</h1>
                </div>
                <div className="toggleBtn">
                    <div>
                    <Link to="/home">
                    <FaHome id="home"/>
                    </Link>
                    </div>

                    <div className="notification">
                    <FaRegBell id="bell"/>
                    <div id="counter">1</div>
                    </div>

                    <div>
                    <Link to="/user-profile">
                    <FaRegUserCircle />
                    </Link>
                    </div>

                    <div>

                    </div>

                </div>
            </div>
        </header>
    )
}

export default Nav;

